const { loadFixture } = require("@nomicfoundation/hardhat-network-helpers");
const { expect } = require("chai");

describe("Constructor exploit", function () {
  async function deployContracts() {
    const Sneaky = await ethers.getContractFactory("Sneaky");
    const sneaky = await Sneaky.deploy(
      ""
    );
    await sneaky.deployed();

    return { sneaky };
  }

  describe("Sneaky works", function () {
    it("Calculates x+2 instead of expected x+1", async function () {
      const { sneaky } = await loadFixture(deployContracts);

      expect(await sneaky.add1(10)).to.equal(12);
    });
  });
});
